func getDeviceName() -> String
{
    if let simulatorModelIdentifier = ProcessInfo().environment["SIMULATOR_MODEL_IDENTIFIER"] { return simulatorModelIdentifier }
    var sysinfo = utsname()
    uname(&sysinfo) // ignore return value
    return String(bytes: Data(bytes: &sysinfo.machine, count: Int(_SYS_NAMELEN)), encoding: .ascii)!.trimmingCharacters(in: .controlCharacters)
}

var globalJsonDecoder: JSONDecoder =
{
    let decoder = JSONDecoder()
    let dateFormatter = DateFormatter()
    dateFormatter.dateFormat = DateManager.FORMAT_FOR_SERVER_LARAVEL
    decoder.dateDecodingStrategy = .formatted(dateFormatter)
    return decoder
}()

var globalJsonEncoder : JSONEncoder =
{
    let encoder = JSONEncoder()
    let dateFormatter = DateFormatter()
    dateFormatter.dateFormat = DateManager.FORMAT_FOR_SERVER_LARAVEL
    encoder.dateEncodingStrategy = .formatted(dateFormatter)
    return encoder
}()

func openUrlInBrowser(url_str:String)
{
    if let url = URL(string: url_str)
    {
        UIApplication.shared.open(url)
    }
}

func getDateInterval(recent: Date, previous: Date) -> (month: Int?, day: Int?, hour: Int?, minute: Int?, second: Int?)
{
    let day = Calendar.current.dateComponents([.day], from: previous, to: recent).day
    let month = Calendar.current.dateComponents([.month], from: previous, to: recent).month
    let hour = Calendar.current.dateComponents([.hour], from: previous, to: recent).hour
    let minute = Calendar.current.dateComponents([.minute], from: previous, to: recent).minute
    let second = Calendar.current.dateComponents([.second], from: previous, to: recent).second
    
    return (month: month, day: day, hour: hour, minute: minute, second: second)
}

